export function addCountryCodes(cities: City[]): City[] {
	const countriesCodes = COUNTRIES_CODES

	// Create a map for faster lookup
	const countryCodeMap = new Map(
		countriesCodes.map(c => [c.country.toLowerCase(), c.countryCode])
	);

	// Add country codes to cities
	const updatedCities = cities.map(city => ({
		...city,
		countryCode: countryCodeMap.get(city.country.toLowerCase())
	}));

	// Print to console
	// console.log(JSON.stringify(updatedCities, null, 2));
	console.log(updatedCities);


	return updatedCities;
}


